    public boolean isPossible_wrongAnswer(int[] nums) {
        if (nums.length < 3 || nums[0] == Integer.MAX_VALUE) {
            return false;
        }
        LinkedList<Integer> startQueue = new LinkedList<>();
        int i = -1;
        int lastNum = Integer.MAX_VALUE;
        while (i + 1 < nums.length) {
            i++;
            if (lastNum != Integer.MAX_VALUE && lastNum != nums[i] - 1) {
                for (int start : startQueue) {
                    if (lastNum - start + 1 < 3) {
                        return false;
                    }
                }
                startQueue.clear();
            }
            int count = 1;
            while (i + 1 < nums.length && nums[i + 1] == nums[i]) {
                i++;
                count++;
            }
            if (startQueue.size() < count) {
                for (int j = 0; j < count - startQueue.size(); j++) {
                    // 这里一开始写的是加入i(下面那个错误也还没发现)，也通过了150 / 180 个通过测试用例（case1），就tm离谱
                    startQueue.addFirst(nums[i]);
                }
            } else if (count < startQueue.size()) {
                for (int j = 0; j < startQueue.size() - count; j++) {
                    int start = startQueue.removeLast();
                    // 这里一开始写的是nums[i]-start+1，也通过了157 / 180 个通过测试用例（case2），就tm离谱
                    if (lastNum - start + 1 < 3) {
                        return false;
                    }
                }
            }
            lastNum = nums[i];
            //System.out.println("lastNum:" + lastNum);
            //System.out.println("startQueue:" + startQueue);
        }
        for (int start : startQueue) {
            if (lastNum - start + 1 < 3) {
                return false;
            }
        }
        return true;
    }

